        case "delete_request":
            return { 
                ...state, 
            }
        case "add_remove_from_cart":
            return { 
                ...state, 
            }
        case "handle_change":
            const name = action.value.target.name;
            const value = action.value.target.value;

            if(Object.values(state.fruitEdit).length > 0){
                //let temp = fruitEdit[0];
                // setFruitEdit([{
