                //     [name]: value
                // }]);
                break;
            }
            else{
                let temp = {[name]: value};
                let fruits = {"fruits" : {...state.fruits, ...temp}};
                return {
                    ...state,
                    ...fruits
                }
            }
        default:
            return state;
    }
}

export default reducer;
