import { createContext } from 'react';

export const initialState = {
    counter: 0
}

export function reducer(state, action){
    switch(action.type){
        case "INCREMENT": 
            return {...state, counter : state.counter + 1};
        case "DECREMENT":
            return {...state, counter: state.counter - 1};
        case "RESET":
            return {
                ...state, counter: initialState.counter
            }
        default:
            return state;
    }
}

