import Wrapper from '../Common/Wrapper';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import CardsList from '../Lib/cards';
import {useState} from 'react';

function Home() {
  const [hoveredCard, setHoveredCard] = useState('');

  return (
    <>
      <Wrapper>
        <Row className="gap-3 p-5" xs={1} md={2} lg={6}>
          {
            CardsList.map((card,key)=>{
              return(
                <Card 
                  key={key} 
                  style={{textAlign: 'center'}}
                  onMouseOver={()=>{
                    setHoveredCard(key);
                  }}
                  onMouseOut={()=>{
                    setHoveredCard('');
                  }}

                >
                  {
                    key === hoveredCard ?
                      <Card.Body className="p-0" style={{backgroundColor: card.backgroundColor}}>
                        <Card.Text>
                          {card.action}
                        </Card.Text>
                      </Card.Body>
                    :
                      <Card.Body>
                        <Card.Header style={{backgroundColor: 'white'}}>{card.icon}</Card.Header>
                        <Card.Title>{card.name}</Card.Title>
                        <Card.Text>
                          {card.title}
                        </Card.Text>
                      </Card.Body>
                  }
                </Card>
              )
            })
          }
        </Row>
      </Wrapper>
    </>
  );
}

export default Home;
