import Wrapper from '../../Common/Wrapper/index';
import {useFetch} from "../../Hooks/useFetch";
import MovieCard from '../../Common/Card/movieCard';
import {useEffect, useState} from "react";

function Index(){
    const [movieDetail, setMovieDetail] = useState("");
    const data = useFetch('https://yts.torrentbay.to/api/v2/list_movies.json');

    useEffect(()=>{
        console.log("movieDetail".movieDetail);
    },[movieDetail]);

    const cardClick = (imdb_code) => {
        let url = `https://yts.torrentbay.to/api/v2/movie_details.json?imdb_id=${imdb_code}`;
        setMovieDetail(url);
    }

    return (
        <div style={{alignItems:'center', justifyContent: 'space-around', display: 'flex', flexDirection: 'column'}}>
            <h2>Movie</h2>

            {
                data !== undefined ?
                <div style={{
                    display: 'flex',
                    flex:1,
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexWrap: 'wrap',
                }}>
                    {          
                        Object.values(data).length > 0 ?
                            data.data.movies.map((movie, key)=>{
                                return (
                                    <MovieCard movie={movie} key={key} cardClick={cardClick} />
                                )
                            })
                        :
                            <p>Loading ...</p>
                    }
                </div>
                :
                    <p>Loading ...</p>
            }
        </div>
    )
}

export default Index;
