import React from 'react';


class ClassComponent extends React.Component {
    constructor() {
        super();
        this.state = {
            course: "React Native",
        }
    }

    changeCourse = () =>{
        this.setState({course: 'React JS'});
    }

    render() {
        return(
            <div>
                <CourseComponent course={this.state.course} />
                <button onClick={this.changeCourse}>Change Course</button>
            </div>
        )
    }
}

class CourseComponent extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            favCourse: 'Flutter'
        }
    }

    static getDerivedStateFromProps(props, state) {
        return {favCourse: props.course};
    }

    componentDidMount() {
        setTimeout(()=>{
            this.setState({favCourse: "Laravel"})
        },[1000])
    }

    render() {
        return <h1>My favourite course is {this.state.favCourse} !</h1>
    }
}

export default ClassComponent;
