const reducer = (state , action) => {
    switch(action.type){
        case "INCREMENT": 
            return {...state, counter : state.counter + 1};
        case "DECREMENT":
            return {...state, counter: state.counter - 1};
        case "RESET":
            return {
                ...state, counter: state.counter
            }
        default:
            return state;
    }
}

export default reducer;
