import {useParams} from 'react-router-dom';
import {useState} from 'react';
import {useFetch} from '../../Hooks/useFetch';

function MovieDetail(){
    const params = useParams();
    let imdb_id = params.movieId;
    let url = `https://yts.torrentbay.to/api/v2/movie_details.json?imdb_id=${imdb_id}`;
    let data = useFetch(url);

    console.log("data",data);
    return (
        <div style={styles.container}>
        {
            data !== undefined ?
                Object.values(data).length > 0 ?
                    <>
                        <div style={styles.title}>
                            <p>{data.data.movie.title_english}</p>
                        </div>

                        <div style={styles.body}>
                            <div style={styles.leftContainer}>
                                <img    
                                    src={`${data.data.movie.large_cover_image}`}
                                />
                            </div>

                            <div style={styles.rightContainer}>
                                <h1>{data.data.movie.title}</h1>
                                <p>Rating: {data.data.movie.rating} </p>
                                <p>Year: {data.data.movie.year} </p>
                                <p>
                                    {
                                        data.data.movie.genres.map((genre,key)=>{
                                            return (
                                                <span key={key}>{genre}</span>
                                            )
                                        })
                                    }
                                </p>
                            </div>
                        </div>
                    </>
                :
                    <p>Loading ...</p>
            :
                <p>Loading ...</p>
        }
        </div>
    )
}

const styles={
    container:{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        flexWrap: 'wrap',
    },
    body: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'flex-start',
        justifyContent: 'center',
        flexWrap: 'wrap',
    },
    leftContainer: {
        display: 'flex',
        flex: 1,
    },
    rightContainer: {
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'flex-start'
    }
}

export default MovieDetail;
