import 'bootstrap/dist/css/bootstrap.min.css';

import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Table from 'react-bootstrap/Table';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Button from 'react-bootstrap/Button';

import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Form from 'react-bootstrap/Form';

import {useState, useEffect} from 'react';

function App() {
  const [colors, setColors] = useState([]);
  const [colorData, setColorData] = useState([]);

  useEffect(()=>{
    let tempColor = localStorage.getItem('colors');
    if(tempColor !== null){
      setColors(JSON.parse(tempColor));
    }
  },[]);

  const handleChange = (e) => {
    let name = e.target.name;
    let checked = e.target.checked;
    let value = e.target.value;

    if(name === "is_popular")
      setColorData(values => ({...values, [name]: checked}));
    if(name !== "is_popular")
      setColorData(values => ({...values, [name]: value}));
  }

  const registerColor = () => {
    if(!colorData.hasOwnProperty('is_popular')){
      setColorData(values => ({...values, ['is_popular']: false}));
    }
    let tempColor = [...colors, colorData];
    localStorage.setItem('colors',JSON.stringify(tempColor));
    setColors(colorData);
  }

  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="#home">Tours of Colors</Navbar.Brand>
        </Container>
      </Navbar>

      <Container>
        <Row>
          <Col sm={12} md={4} lg={6}>
            <Form onSubmit={()=>{registerColor()}}>
              <FloatingLabel
                controlId="floatingInput"
                label="Color Name"
                className="mb-3"
              >
                <Form.Control name="color_name" type="text" placeholder="Color Name" onChange={(e)=> {handleChange(e)}} />
              </FloatingLabel>

              <Form.Check 
                type="switch"
                id="custom-switch"
                label="Check this switch"
                name="is_popular"
                onChange={(e)=> {handleChange(e)}}
              />

              <FloatingLabel controlId="floatingPassword" label="Color code">
                <Form.Control name="color_code" type="text" placeholder="Color Code" onChange={(e)=> {handleChange(e)}} />
              </FloatingLabel>

              <Button variant="primary" type="submit">
                Register Color
              </Button>
            </Form>
          </Col>

          <Col sm={12} md={8} lg={6}>
            <Container>
              <Table striped>
                <thead>
                  <tr>
                    <th>#</th>
                    <th></th>
                    <th>Color Name</th>
                    <th>Is Popular ?</th>
                    <th colspan="2">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {
                    Object.values(colors).length > 0 &&
                      Object.values(colors).map((color,key)=>{ 
                        return (
                          <tr key={key}>
                            <td>{key + 1}.</td>
                            <td><div style={{backgroundColor: color.color_code, height: 20, borderRadius: 50, width: 20}}></div></td>
                            <td>{color.color_name}</td>
                            <td>{color.is_popular === true ? "True" : "False"}</td>
                            <td>
                              <Button variant="success" type="submit">
                                Edit
                              </Button>
                            </td>
                            <td>
                            <Button variant="danger" type="submit">
                                Delete
                              </Button>
                            </td>
                          </tr>
                        )
                      })
                  }
                </tbody>
              </Table>
            </Container>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
