import {useState, useEffect} from 'react';
import axios from "axios";

const useFetch = (url) => {
    const [dataFromApi, setDataFromApi] = useState([]);

    useEffect(()=>{
        getData();
    },[url]);

    const getData = async() => {
        let data = await axios.get(url).then((res)=>{
            return res.data;
        })
        .error((err)=>{console.log("err",err)});

        setDataFromApi(data);
    }

    return dataFromApi;
}

export  {useFetch};
