import {useContext} from 'react';
import {FruitContext} from '../../Context/fruitContext';
import {action} from './actionTypes';

function Create(){
   const [state, dispatch] = useContext(FruitContext);

    let {error} = state;
    let {
        fruitEdit: update,
        fruits,
    } = state;

   return (
        <>
            {
                Object.values(error).length > 0 &&
                    <span>{error.name}</span>
            }

            <input
                name="image"
                type="text"
                placeholder="Fruit Image"
                value={
                    Object.values(update).length > 0 ?
                        update[0].image
                    :
                        fruits.image    
                }
                onChange={
                    (e)=>{
                        dispatch({
                            type: action.HANDLE_CHANGE,
                            value: e
                        })
                    }
                }
            />

            <input
                name="name"
                type="text"
                placeholder="Fruit Name"
                value={
                    Object.values(update).length > 0 ?
                        update[0].name
                    :
                        fruits.name  
                }
                onChange={
                    (e)=>{
                        dispatch({
                            type: action.HANDLE_CHANGE,
                            value: e
                        })
                    }
                }
            />

            <br />

            <button 
                onClick={(e)=>{
                    dispatch({
                        type: action.ADD_REQUEST
                    })
                }}
            >
                {
                    Object.values(update).length > 0 ?
                        "Update"
                    :
                        "Register"
                }
            </button>

            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Fruit Image</Form.Label>
                    <Form.Control type="text" placeholder="Fruit Image" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Fruit Name</Form.Label>
                    <Form.Control type="text" placeholder="Fruit Name" />
                </Form.Group>

                <Button 
                    variant="primary" 
                    type="submit"
                    onClick={(e)=>{
                        dispatch({
                            type: action.ADD_REQUEST
                        })
                    }}
                >
                    {
                        Object.values(update).length > 0 ?
                            "Update"
                        :
                            "Register"
                    }
                </Button>
            </Form>
        </>
    )   
}

export default Create;
