import Wrapper from '../../Common/Wrapper/index';
import {useFetch} from "../../Hooks/useFetch";
import MovieCard from '../../Common/Card/movieCard';
import { useNavigate } from "react-router-dom";
import genres from '../../Lib/genres';
import Pill from "../../Common/Pill";
import {useState} from 'react';
import axios from 'axios';

function Index(){
    let url = 'https://yts.torrentbay.to/api/v2/list_movies.json?limit=50&genre=horror';
    const [movies, setMovies] = useState([]);
    const navigate = useNavigate();
    const data = useFetch(url);

    const cardClick = (imdb_code) => {
        navigate(`${imdb_code}`);
    };

    const changeGenre = async (genre) => {
        url = `https://yts.torrentbay.to/api/v2/list_movies.json?limit=50&genre=${genre}`;
        data = await axios.get(url).then((res)=>{
            return res.data;
        });
    } 

    return (
        <div style={{alignItems:'center', justifyContent: 'space-around', display: 'flex', flexDirection: 'column'}}>
            <h2>Movie</h2>

            <div style={styles.container}>
            {
                genres.map((genre, key)=>{
                   return (
                    <Pill 
                        onPress={changeGenre}
                        genre={genre} 
                        key={key} 
                        style={{
                            display: 'flex',
                            borderRadius: 10,
                            backgroundColor: `${genre.colorCode}`,
                            alignItems: 'center',
                            justifyContent: 'center',
                            width :100,
                            margin: 10,
                            cursor: 'pointer',
                        }}
                    />
                   )
                })
            }
            </div>

            {
                data !== undefined ?
                <div style={{
                    display: 'flex',
                    flex:1,
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexWrap: 'wrap',
                }}>
                    {          
                        Object.values(data).length > 0 ?
                            data.data.movies.map((movie, key)=>{
                                return (
                                    <MovieCard movie={movie} index={key} cardClick={cardClick} />
                                )
                            })
                        :
                            <p>Loading ...</p>
                    }
                </div>
                :
                    <p>Loading ...</p>
            }
        </div>
    )
}

const styles = {
    container: {
        display: 'flex',
        flex:1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        flexWrap: 'wrap',
    }
}

export default Index;
