const reducer = (state, action) => {
    switch(action.type){
        case "list_request": 
            return {
                ...state, 

            };
        case "add_request":
            let fruitList = [{...state.fruits, ...state.fruitList}];
            state.fruits = {
                name: "",
                image: "",
            };
            state.toast = true;
            state.message = 'Added Successfully !';
            return {
                ...state, 
                fruitList
            };
        case "edit_request":
            let fruitEdit = state.fruitList.filter((tmp)=>{return tmp.name === action.value});
            state.editedKey = action.key;
            state.toast = true;
            state.message = 'Updated successfully !';
            return { 
                ...state, 
                fruitEdit,

            }
        case "delete_request":
            return { 
                ...state, 
            }
        case "add_remove_from_cart":
            return { 
                ...state, 
            }
        case "handle_change":
            const name = action.value.target.name;
            const value = action.value.target.value;

            if(Object.values(state.fruitEdit).length > 0){
                let temp = {[name]: value};
                let fruits = {"fruits" : {...state.fruits, ...temp}};
                return {
                    ...state,
                    ...fruits
                }
            }
            else{
                let temp = {[name]: value};
                let fruits = {"fruits" : {...state.fruits, ...temp}};
                return {
                    ...state,
                    ...fruits
                }
            }
        default:
            return state;
    }
}

export default reducer;
