import React from 'react';


class ClassComponent extends React.Component {
    constructor() {
        super();
        this.state = {
            course: "React Native",
        }
    }

    render() {
        return(
            <div>
                <CourseComponent course={this.state.course} />
            </div>
        )
    }
}

class CourseComponent extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            favCourse: 'Flutter'
        }
    }

    // static getDerivedStateFromProps(props, state) {
    //     return {favCourse: props.course};
    // }

    changeCourse = () =>{
        this.setState({course: 'React JS'});
    }

    render() {
        return (
            <>
                <h1>My favourite course is {this.state.favCourse} !</h1>
                <button onClick={this.changeCourse}>Change Course</button>
            </>
        )
    }
}

export default ClassComponent;
